export default (text) => {
    const actionables = text
        .split(' ')
        .map((p) => p.trim())
        .filter(String);
    const pipe = actionables.indexOf('|');
    const done = actionables.splice(pipe).filter((t) => t !== '|');
    return {
        actionables,
        done,
        get keywords() {
            return actionables.concat(done);
        },
    };
};
